mapper.insert:
INSERT INTO file_chunks (
    file_id, file_name, file_size, file_md5, chunk_number, total_chunks,
    chunk_size, chunk_md5, upload_id, bucket_name, object_name, status, uploaded_at
) VALUES (
    #{fileId}, #{fileName}, #{fileSize}, #{fileMd5}, #{chunkNumber}, #{totalChunks},
    #{chunkSize}, #{chunkMd5}, #{uploadId}, #{bucketName}, #{objectName}, #{status}, #{uploadedAt}
)

mapper.select:
SELECT * FROM file_chunks 
WHERE file_id = #{fileId} 
ORDER BY chunk_number ASC

SELECT * FROM file_chunks 
WHERE file_id = #{fileId} AND status = #{status}
ORDER BY chunk_number ASC
